package WikipediaService;import com.google.gson.Gson;import com.google.gson.JsonElement;import com.google.gson.JsonObject;import java.util.Map;import java.util.Set;class FormatHelperJSON implements FormatHelper {    @Override public String formatToString(String jsonTerm){        Gson gson = new Gson();        JsonObject jobj = gson.fromJson(jsonTerm, JsonObject.class);        JsonObject query = jobj.get("query").getAsJsonObject();        JsonObject pages = query.get("pages").getAsJsonObject();        Set<Map.Entry<String, JsonElement>> pagesSet = pages.entrySet();        Map.Entry<String, JsonElement> first = pagesSet.iterator().next();        JsonObject page = first.getValue().getAsJsonObject();        JsonElement extract = page.get("extract");        return replaceFakeEntersWithRealEnters(extract);    }    private String replaceFakeEntersWithRealEnters(JsonElement extract){        if (extract != null) {            return extract.getAsString().replace("\\n", "\n");        }        return null;    }}